mapper.select:
SELECT 

FROM nc_pharmacy a

WHERE a.id = #{id}

SELECT
FROM nc_pharmacy a
AND a.pharmacy_name LIKE '%'||#{pharmacyName}||'%' '%'+#{pharmacyName}+'%' concat('%',#{pharmacyName},'%')
AND a.corp_name = #{corpName}
ORDER BY ${page.orderBy}

SELECT 

FROM nc_pharmacy a






		ORDER BY ${page.orderBy}

mapper.update:
UPDATE nc_pharmacy SET 	
	nc_id = #{ncId},
	pharmacy_id = #{pharmacyId},
	pharmacy_name = #{pharmacyName},
	corp_name = #{corpName}
WHERE id = #{id}
